%% GetColLayout  -  dev
%   "helper" function which returns different version of COL_LAYOUT
%   depending a string
%                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                   

function [COL_LAYOUT,COL_COUNT,COLS] = GetColLayout(version)

    if strcmp(version, '5 cols') 
        COL_COUNT = 5;

        COL_LAYOUT.xcoords = 1;
        COL_LAYOUT.ycoords = 2;
        COL_LAYOUT.area = 3;
        COL_LAYOUT.covarience = 4;
        COL_LAYOUT.distance = 5;
    
    elseif(strcmp(version, '4 cols'))
        COL_COUNT = 4;

        COL_LAYOUT.xcoords = 1;
        COL_LAYOUT.ycoords = 2;
        COL_LAYOUT.area = 3;
        COL_LAYOUT.covarience = 4;
    end  
        
        
    COLS = (fieldnames(COL_LAYOUT));
end

